import React, { useState } from 'react';
import './App.css';
import makeStyles from "@material-ui/core/styles/makeStyles";
import Navigation from "./components/Navigation";
import Questions from "./components/Questions";
import Question from "./components/Question";
import {Router} from '@reach/router';
import {Link} from "@reach/router";
import AddQuestion from './components/AddQuestion';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow : 1
    },
    paper: {
        padding: theme.spacing(1),
        minWidth : 350,
        minHeight : 200,
        maxWidth : 500,
        display : 'flex',
        flexDirection : 'column',
        color: theme.palette.text.secondary,
    },
    questionGrid : {
        display : 'flex',
        justifyContent : 'space-around',
        marginTop : 30,
        marginBottom : 30
    },
    text : {
        padding: 10
    }
}));

export default function App() {
const classes = useStyles();

    document.title = "Stack Overflow";

    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Angular',
            upVote : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'React',
            upVote : 0,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        }
    ]);

        const upVote = index => {
        const newVote = [...questions];
        newVote[index].upVote++
        setQuestions(newVote);
    };


    const addQuestion = value => {
        const newQuestions = [...questions, value ];
        setQuestions(newQuestions);
    };

    function getquestionid(id)  {
       return questions.find(q => q.id === id.Number)
    }

    return (
    <div className={classes.root}>
            <Navigation />
            <Link to="/addQuestion"><button>AddQuestion</button></Link>
        <Router>
            <Questions
                path="/" 
                questions={questions} 
                setQuestions={setQuestions} 
                upvote={upVote}
            />
            <Question 
                path="/question/:id"
                getquestionid={id => getquestionid(id)}
                questions={questions}
            />
            <AddQuestion 
                path="/addQuestion"
                addQuestion={addQuestion}
            />
        </Router>
    </div>
  );
}
