import React from 'react';
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Card from "@material-ui/core/Card";
import Button from "@material-ui/core/Button";
import Typpgraphy from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import {Link} from "@reach/router";
import makeStyles from "@material-ui/core/styles/makeStyles";

import ArrowCounter from './ArrowCounter';

const useStyles = makeStyles( theme =>({
    card : {
        maxWidth : 500,
        minWidth : 500,
        maxHeight : 250,
        minHeight : 250,
        marginBottom : 40,
        display : 'flex',
        justifyContent : 'center',
        alignItems : 'center',
        flexDirection : 'column', 
        margin : 5,
        padding : 20
    },
    title : {
        display : 'flex',
        background : 'whitesmoke',
        borderRadius : 3,
        padding : 20,
        position : 'absolute',
        marginTop : -140,
    },
    Grid : {
        display: 'flex',
        alignItems :'center',
        flexDirection : 'row',
        flexWrap : 'wrap'
    },
    Typography : {
        marginTop : 20
    },
    button : {
        margin: theme.spacing(1),
    },
    icons : {

    },
    negativeIcon : {
        color : 'red'
    },
    positiveIcon : {
        color : 'green'
    }
}));

function Questions({questions, upVote, downVote}){

    const classes = useStyles();


    let content = (!questions) ? <div>...Something went wrong</div> : 

        <Grid container justify="center" alignItems="center" xs={12}> 
            <Typpgraphy className={classes.Typography} variant="h3" component="h2" >Questions</Typpgraphy>
        <Grid justify="center" alignItems="center" container item xs={12} spacing={1} className={classes.Grid}>
        {questions.map((question, index) => {

            return (        
                <Box>
                        <Card className={classes.card}>
                            <div className={classes.title}>
                                <Typography variant="p" component="p">{question.title}</Typography>
                                    <Typography variant="p" component="p">{question.subtitle}</Typography>
                            </div>
                                        <Link 
                                            className={classes.paper}
                                            id={question._id}
                                            key={index}
                                            index={index}
                                            to={`/question/${question._id}`}><Button color="primary" className={classes.button}>See more</Button>    
                                        </Link> 
                                    <p>Votes: {question.upVote}</p>
                                <ArrowCounter
                                    upVote={upVote}
                                    downVote={downVote}
                                    classes={classes}
                                    question={question}
                                    index={index}
                                />
                        </Card>
                </Box>
                )
            }
        )}
        </Grid>
    </Grid>
    return content;
}

export default Questions;
