import React, { useState } from 'react';
import Button from "@material-ui/core/Button";
import {Typography} from "@material-ui/core";
import makeStyles from "@material-ui/core/styles/makeStyles";

const useStyles = makeStyles({
    button : {
        marginTop : 20,
    }
})


const AskQuestion = (props) => {

    //const [title, setTitle] = useState('');
    const [state, setState] = useState({

        title : '',
        subtitle : '',
        category : '',
        up : 0,
        answers : [],
    })

    //add State to questions array
    const onAddItem = () => {
       const newArray = [...props.questions.concat(state)];
        return console.log(newArray);

    };

    console.log(state);
    console.log(props.questions);


const classes = useStyles();
    return (
        <div>
            <Typography>Ask a question</Typography>
            <form className="form">
                <input type="text" className="form-input"  placeholder="Enter title"
                    onChange={event => setState({ title : event.target.value})}
                />
                <input type="text" className="form-input"  placeholder="Enter subtitle"
                       onChange={event => setState({subtitle : event.target.value})}
                />
                <input type="text" className="form-input"  placeholder="Enter subtitle"
                       onChange={event => setState({category : event.target.value})}
                />
                <Button className={classes.button} variant="contained" color="primary"
                    onClick={onAddItem}
                >Submit
                </Button>
                <Typography component="p">{state.title}</Typography>
            </form>
        </div>
    )
}

export default AskQuestion;
