import React from 'react';
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Container from "@material-ui/core/Container";
import Typpgraphy from "@material-ui/core/Typography";
import {Link} from "@reach/router";
import makeStyles from "@material-ui/core/styles/makeStyles";

function Questions({questions, upVote}){

const useStyles = makeStyles(({
    paper : {
        maxWidth : 500,
        display : 'flex',
        justifyContent : 'center',
        alignItems : 'center',
        flexDirection : 'column', 
        marginTop : 20
    },
    Grid : {
        display: 'flex',
        alignItems :'center',
        flexDirection : 'row',

    }
}))


    return(
        <Container maxWidth="xl"> 
            <Typpgraphy variant="h3" component="h2" >Questions</Typpgraphy>
        <Grid  container item xs={12} spacing={3}>
        {questions.map((question, index) => {
            return (    

                    <Paper >
                        <Link 
                            id={question.id}
                            key={index}
                            question={question}
                            index={index}
                            to={`/question/${question.id}`}>{question.title}     
                        </Link> 
                            <p onClick={() => upVote(index)}>{question.upVote}</p>   
                    </Paper>

                )
        }
        )}
        </Grid>

    </Container>
    )
}


export default Questions;
