import React, {useState} from 'react';
import './App.css';
import Navigation from "./components/Navigation";



function AddQuestion({addQuestion}){
    const [value, setValue] = useState({
        title : '',
        subtitle : '',
        category : '',
        upVotes : 0,
        isCompleted : false,
    });
    const handleSubmit = e => {
        e.preventDefault();
            addQuestion(value);
            setValue('');
    };

    const onChange = (event) => {
        setValue({ ...value, [event.target.name] : event.target.value});
    }

    return(
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                value={value.title}
                name="title"
                onChange={onChange}
                placeholder="Enter your title"
            />
            <input
                type="text"
                value={value.subtitle}
                name="subtitle"
                onChange={onChange}
                placeholder="Enter your subtitle"
            />
            <input
                type="text"
                value={value.category}
                name="category"
                onChange={onChange}
                placeholder="Choose category"
            />
            <button onClick={handleSubmit}>Submit</button>
        </form>

    )
}


function Question({ question, index, UpVote}){

    return(
        <div key={index}>
            <p>{question.category}</p>
            <h3>{question.title}</h3>
            <p>{question.subtitle}</p>
            <p onClick={UpVote}>{question.upVote}</p>
        </div>
    )
}

export default function App() {

    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'Databases',
            upVote : 4,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        }
    ]);

    function UpVote(index){
        console.log("was pressed");
        const damn = [...questions];
        damn[index].upVote++;
        return setQuestions(damn);
    }



    const addQuestion = text => {
        const newQuestions = [...questions, text ];
        setQuestions(newQuestions);
        console.log(newQuestions);
    };

    return (
    <div className="App">
        <Navigation />
        <div>
            {questions.map((question, index) => {
                return (
                    <Question
                        key={index}
                        index={index}
                        question={question}
                        UpVote={UpVote}
                    />
                    )
            })}
        </div>
        <AddQuestion addQuestion={addQuestion} />
    </div>
  );
}
