import React, {useState} from 'react';
import './App.css';

import Navigation from "./components/Navigation";
import AddQuestion from "./components/AddQuestion";
import Question from "./components/Question";

export default function App() {

    document.title = "Stack Overflow";
    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'Databases',
            upVote : 0,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        }
    ]);

    //Try in question function component
    const upVote = id => {
        const newVote = [...questions];
        newVote[id].upVote++;
        setQuestions(newVote);
    };


    const addQuestion = value => {
        const newQuestions = [...questions, value ];
        setQuestions(newQuestions);
        console.log(newQuestions);
    };


    let newArray = questions.filter(function (el) {
        return el.id <= 1;
    });

    let javascriptCategory = questions.filter(function (el) {
        return el.category === "Javascript";
    })
    console.log(newArray);
    console.log(javascriptCategory);



    return (
    <div className="App">
        <Navigation />
        <div className="questions-list">
            {questions.map((question, index) => {
                return (
                    <Question
                        key={question.id}
                        index={index}
                        question={question}
                        upVote={upVote}
                        questions={questions}
                    />
                    )
            })}
        </div>
        <h1>Javascript</h1>
        <div>
            {javascriptCategory.map((jav,index) => {
                return (
                    <li
                        key={index}
                    >
                        {jav.title}
                    </li>
                )
            })}
        </div>
        <AddQuestion addQuestion={addQuestion} />
    </div>
  );
}
