import React, {useState} from 'react';
import './App.css';

import Navigation from "./components/Navigation";
import AddQuestion from "./components/AddQuestion";
import Question from "./components/Question";

export default function App() {

    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'Databases',
            upVote : 0,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        }
    ]);

    const upVote = index => {
        const newVote = [...questions];
        newVote[index].upVote++;
        setQuestions(newVote);
    };


    const addQuestion = text => {
        const newQuestions = [...questions, text ];
        setQuestions(newQuestions);
        console.log(newQuestions);
    };


    return (
    <div className="App">
        <Navigation />
        <div>
            {questions.map((question, index) => {
                return (
                    <Question
                        key={index}
                        index={index}
                        question={question}
                        upVote={upVote}
                        questions={questions}
                    />
                    )
            })}
        </div>
        <AddQuestion addQuestion={addQuestion} />
    </div>
  );
}
