const classes = useStyles();
    return (
        <div>
            <Typography>Ask a question</Typography>
            <form className="form">
                <input type="text" className="form-input"  placeholder="Enter title"
                    onChange={event => setTest({ title : event.target.value})}
                />
                <input type="text" className="form-input"  placeholder="Enter subtitle"
                      // onChange={event => setState({subtitle : event.target.value})}
                />
                <input type="text" className="form-input"  placeholder="Enter Category"
                       //onChange={event => setState({category : event.target.value})}
                />
                <Button className={classes.button} variant="contained" color="primary"
                    onClick={addItem}
                >Submit
                </Button>
                <Typography component="p"></Typography>
            </form>
        </div>
    )
}

export default AskQuestion;
