import React, {useState} from 'react';
import './App.css';
import Grid from "@material-ui/core/Grid";
import makeStyles from "@material-ui/core/styles/makeStyles";

import Navigation from "./components/Navigation";
import AddQuestion from "./components/AddQuestion";
import Question from "./components/Question";


const useStyles = makeStyles(theme => ({
    root: {
        flexGrow : 1
    },
    paper: {
        padding: theme.spacing(1),
        minWidth : 350,
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

export default function App() {
const classes = useStyles();

    document.title = "Stack Overflow";
    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'Databases',
            upVote : 0,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        }
    ]);

    //Try in question function component
    const upVote = id => {
        const newVote = [...questions];
        newVote[id].upVote++;
        setQuestions(newVote);
    };


    const addQuestion = value => {
        const newQuestions = [...questions, value ];
        setQuestions(newQuestions);
        console.log(newQuestions);
    };

    //Categorizing questions
    let javascriptCategory = questions.filter(function (el) {
        return el.category === "Javascript";
    });




    return (
    <div className={classes.root}>
        <Navigation />
        <h1>All questions</h1>
        <Grid container spacing={1}>
            <Grid container item xs={12} spacing={3}>
            {questions.map((question, index) => {
                return (
                        <Question
                        key={question.id}
                        index={index}
                        question={question}
                        upVote={upVote}
                        questions={questions}
                        classes={classes}
                        />
                    )
            })}
            </Grid>
        </Grid>
        <h1>Javascript related</h1>
        <div>
            {javascriptCategory.map((jav,index) => {
                return (
                    <li
                        key={index}
                    >
                        {jav.title}
                    </li>
                )
            })}
        </div>
        <AddQuestion addQuestion={addQuestion} />
    </div>
  );
}
