import React, {useState} from 'react';
import './App.css';
import Navigation from "./components/Navigation";
import Questions from "./components/Questions";
import Question from "./components/Question";
import AskQuestion from "./components/AskQuestion";

    const introMessage = "Welcome to developers Q/A Community!";
    const introSubMessage = "Feel free to help or get help for/from community members!";

function App() {

    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            up : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Javascript',
            up : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'Databases',
            up : 4,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            up : 0,
            answers : ['']
        }
    ]);

    const [newArray, setArray] = useState({
        title: '',
    })

    const addQuestion = () => {
        const newArray = [...questions.concat(newArray)];
        return console.log(newArray);
    }

    return (
    <div className="App">
        <input type="text" onChange={event => setArray({title : event.target.value})} />
        <button onClick={addQuestion}>Submit</button>
        <Navigation />
      <h1>{introMessage}</h1>
      <p>{introSubMessage}</p>
        <Questions questions={questions} />
        <Question questions={questions} />
        <AskQuestion questions={questions} />
    </div>
  );
}

export default App;
