import React, {useState} from "react";

function AddQuestion({addQuestion, questions}){
    const [value, setValue] = useState({
        title : '',
        subtitle : '',
        category : '',
        upVotes : 0,
        isCompleted : false,
    });


    const handleApi = e => { 
        e.preventDefault();
        fetch('http://localhost:8080/api/v1/questions', {
         method: 'post',
         headers: {'Content-Type':'application/json'},
         body: {
            "title": title,
            "subtitle" : subtitle,
            "category" : category
         }
        });
       };

    const onChange = (event) => {
        setValue({ ...value, [event.target.name] : event.target.value});
    }

    //Should be in seperate component addQuestion, with reach router path

    return(
            <form onSubmit={handleApi}>
                <input
                    value={value.title}
                    name="title"
                    onChange={onChange}
                    placeholder="Enter your title"
                />
                <input 
                    value={value.subtitle}
                    name="subtitle"
                    onChange={onChange}
                    placeholder="Enter your subtitle"
                />
                <input         
                    value={value.category}
                    name="category"
                    onChange={onChange}
                    placeholder="Choose category"
                />
                <input         
                    value={value.upVotes}
                    name="upVotes"
                    onChange={onChange}
                    placeholder="Votes"
                />


                <button onClick={handleApi}>API Button</button>
            </form>
    )
}

export default AddQuestion;
