import React from 'react';
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Card from "@material-ui/core/Card";
import Button from "@material-ui/core/Button";
import Typpgraphy from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import {Link} from "@reach/router";
import makeStyles from "@material-ui/core/styles/makeStyles";

const useStyles = makeStyles( theme =>({
    card : {
        maxWidth : 500,
        minWidth : 250,
        display : 'flex',
        justifyContent : 'center',
        alignItems : 'center',
        flexDirection : 'column', 
        margin : 5,
        padding : 20
    },
    Grid : {
        display: 'flex',
        alignItems :'center',
        flexDirection : 'row',
    },
    Typography : {
        marginTop : 20
    },
    button : {
        margin: theme.spacing(1),
    }
}));

function Questions({questions, upVote}){

    const classes = useStyles();

    return(
        <Grid container justify="center" alignItems="center" xs={12}> 
            <Typpgraphy className={classes.Typography} variant="h3" component="h2" >Questions</Typpgraphy>
        <Grid justify="center" alignItems="center" container item xs={12} spacing={1} className={classes.Grid}>
        {questions.map((question, index) => {
            return (    
                <Box>
                        <Card className={classes.card}>
                                <Typography variant="p" component="p">{question.title}</Typography>
                                <Link 
                                    className={classes.paper}
                                    id={question.id}
                                    key={index}
                                    question={question}
                                    index={index}
                                    to={`/question/${question.id}`}><Button color="primary" className={classes.button}>See more</Button>    
                                </Link> 
                                <p onClick={() => upVote(index)}>Votes: {question.upVote}</p>

                        </Card>

                </Box>
                )
            }
        )}
        </Grid>
    </Grid>
    )
}


export default Questions;
