import React from 'react';
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import Typography from "@material-ui/core/Typography"
import makeStyles from "@material-ui/core/styles/makeStyles";
import { CardContent, CardHeader, CardActions,  } from '@material-ui/core';
import IconButton from "@material-ui/core/IconButton";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";


const useStyles = makeStyles(({
    card : {
        display : 'flex',
        flexDirection : 'column', 
        alignItems : 'center',
        justifyContent : 'center',
        marginTop : 20,
    },
    Grid : {
        display: 'flex',
        alignItems :'center',
        flexDirection : 'row',
    },
    Box : {
        backgroundColor : 'red',
    },
    spaceBetween : {
        display: 'flex',
        justifyContent : 'space-between'
    }
}))


function Question({getquestionid, id}) {

    const classes = useStyles();
    const question = getquestionid(parseInt(_id));

    return(
        <Grid container space={1} justify="center" alignItems="center">
            <Grid item>
                <Card  className={classes.card}>
                    <CardHeader
                        title={question.title}
                        subheader={question.category}
                    >
                    </CardHeader>
                    <CardContent >
                        <Typography variant="h6" color="textSecondary" component="p">
                            Question: {question.subtitle}
                        </Typography>
                        <Typography variant="h4" color="textSecondary" component="p">
                            {question.upVote}
                        </Typography>
                    </CardContent>
                    <CardActions disableSpacing>
                        <IconButton aria-label="add to favorites">
                            <FavoriteIcon />
                        </IconButton>
                        <IconButton aria-label="share">
                            <ShareIcon />
                        </IconButton>
                    </CardActions>
                </Card>     
            </Grid>
        </Grid>

    )
}

export default Question;
