import React, {useState} from 'react';
import './App.css';
import Navigation from "./components/Navigation";



function AddQuestion({addQuestion}){
    const [value, setValue] = useState({title : '', subtitle : ''});
    const handleSubmit = e => {
        e.preventDefault();
        if(!value) return;
        addQuestion(value);
        setValue('');
    }

    return(
        <form onSubmit={handleSubmit}>
            <input
                type="title"
                value={value.title}
                onChange={event => setValue({title : event.target.title})}
                placeholder="Submit your question"
            />
            <button onClick={handleSubmit}>Submit</button>
        </form>

    )
}


function Question({ question, index }){
    return(
        <div key={index}>
            <h3>{question.title}</h3>
            <p>{question.subtitle}</p>
        </div>
    )
}

export default function App() {

    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'Databases',
            upVote : 4,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        }
    ]);

    const addQuestion = text => {
        const newQuestions = [...questions, { text }];
        setQuestions(newQuestions);
        console.log(newQuestions);
    };

    return (
    <div className="App">
        <Navigation />
        <div>
            {questions.map((question, index) => {
                return (
                    <Question
                        key={index}
                        index={index}
                        question={question}
                    />
                    )
            })}
        </div>
        <AddQuestion addQuestion={addQuestion} />
    </div>
  );
}
