import React, { useState } from 'react';
import './App.css';
import Navigation from "./components/Navigation";
import Questions from "./components/Questions";
import Question from "./components/Question";
import AskQuestion from "./components/AskQuestion";

    const introMessage = "Welcome to developers Q/A Community!";
    const introSubMessage = "Feel free to help or get help for/from community members!";

function App(props) {

    const [title,setTitle] = useState('');
    const [subtitle,setSubtitle] = useState('');
    const [category,setCategory] = useState('');

    const handleTitle = (e) =>{
        e.preventDefault();
        let newTitle = e.target.value;
        setTitle(newTitle);
    }

    const handleSubtitle = (e) =>{
        e.preventDefault();
        let newSubtitle = e.target.value;
        setSubtitle(newSubtitle);
    }

    const handleCategory = (e) =>{
        e.preventDefault();
        let newCategory = e.target.value;
        setCategory(newCategory);
    }

    const questionsList = [
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            up : 0,
            answers : ['']

        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Javascript',
            up : 0,
            answers : ['']

        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'Databases',
            up : 4,
            answers : ['']

        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            up : 0,
            answers : ['']

        }
    ]



    return (
    <div className="App">
        <Navigation />
      <h1>{introMessage}</h1>
      <p>{introSubMessage}</p>
        <Questions questions={questionsList} />
        <Question questions={questionsList} />
        <AskQuestion handleCategory={handleCategory()} handleSubtitle={handleSubtitle()} handelTitle={handleTitle()} />
    </div>
  );
}

export default App;
