import React, { useState} from 'react';
import './App.css';
import Grid from "@material-ui/core/Grid";
import makeStyles from "@material-ui/core/styles/makeStyles";

import Navigation from "./components/Navigation";
import AddQuestion from "./components/AddQuestion";
import Question from "./components/Question";
import Questions from "./components/Questions";



const useStyles = makeStyles(theme => ({
    root: {
        flexGrow : 1
    },
    paper: {
        padding: theme.spacing(1),
        minWidth : 350,
        minHeight : 200,
        maxWidth : 500,
        display : 'flex',
        flexDirection : 'column',
        color: theme.palette.text.secondary,
    },
    questionGrid : {
        display : 'flex',
        justifyContent : 'space-around',
        marginTop : 30,
        marginBottom : 30
    },
    text : {
        padding: 10
    }
}));

export default function App() {
const classes = useStyles();

    document.title = "Stack Overflow";

    const [questions, setQuestions] =  useState([
        {
            id : 0,
            title : 'What is Javascript',
            subtitle : 'I need more information about it',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        },
        {
            id : 1,
            title : 'How to do map() function',
            subtitle : 'Can someone tell how to do it step by step',
            category : 'Angular',
            upVote : 0,
            answers : ['']
        },{
            id : 2,
            title : 'MongoDB or firebase?',
            subtitle : 'Cant decide...',
            category : 'React',
            upVote : 0,
            answers : ['']
        },{
            id : 3,
            title : 'Any good tutorials for ES6',
            subtitle : 'Want to start practice, but dunno where',
            category : 'Javascript',
            upVote : 0,
            answers : ['']
        }
    ]);

        const upVote = index => {
        const newVote = [...questions];
        newVote[index].upVote++
        setQuestions(newVote);
    };


    const addQuestion = value => {
        const newQuestions = [...questions, value ];
        setQuestions(newQuestions);
    };
    //Categorizing questions --End

    return (
    <div className={classes.root}>
        <Navigation />
        <Questions questions={questions} classname={classes.questions} />
        <Grid container spacing={1}>
            <Grid className={classes.questionGrid} container item xs={12} spacing={3}>
            {questions.map((question, index) => {
                return (

                    <Question
                            classname={classes.question}
                            key={index}
                            index={index}
                            question={question}
                            upVote={upVote}
                            classes={classes}
                            questions={questions}
                        />
                    )
            })}
            </Grid>
        </Grid>
        <AddQuestion addQuestion={addQuestion} />
    </div>
  );
}
