import React from 'react';
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Container from "@material-ui/core/Container";
import Typpgraphy from "@material-ui/core/Typography";
import {Link} from "@reach/router";
import makeStyles from "@material-ui/core/styles/makeStyles";

const useStyles = makeStyles(({
    paper : {
        maxWidth : 350,
        display : 'flex',
        justifyContent : 'center',
        alignItems : 'center',
        flexDirection : 'column', 
        margin : 20,
        padding : 50
    },
    Grid : {
        display: 'flex',
        alignItems :'center',
        flexDirection : 'row',
    },
    Typography : {
        marginTop : 20
    }
}));

function Questions({questions, upVote}){

    const classes = useStyles();

    return(
        <Container maxWidth="xl"> 
            <Typpgraphy className={classes.Typography} variant="h3" component="h2" >Questions</Typpgraphy>
        <Grid  container item xs={12} spacing={3} className={classes.Grid}>
        {questions.map((question, index) => {
            return (    

                    <Paper className={classes.paper} >
                        <Link 
                            className={classes.paper}
                            id={question.id}
                            key={index}
                            question={question}
                            index={index}
                            to={`/question/${question.id}`}>{question.title}     
                        </Link> 
                            <p onClick={() => upVote(index)}>{question.upVote}</p>   
                    </Paper>

                )
        }
        )}
        </Grid>

    </Container>
    )
}


export default Questions;
