import React, {useState} from "react";

function AddQuestion({addQuestion}){
    const [value, setValue] = useState({
        title : '',
        subtitle : '',
        category : '',
        upVotes : 0,
        isCompleted : false,
    });

    const url = 'http://localhost:8080/api/v1/questions';

    //Posting input state to api
    const handleApi = e => { 
        e.preventDefault();
        (async () => {
            const rawResponse = await fetch(url, {
              method: 'POST',
              headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
              },
              body: JSON.stringify({
                title: value.title,
                subtitle: value.subtitle,
                category : value.category
            })
            });
            const content = await rawResponse.json();

            console.log(content);
          })();
       };

    const onChange = (event) => {
        setValue({ ...value, [event.target.name] : event.target.value});
    }

    //Should be in seperate component addQuestion, with reach router path

    return(
            <form onSubmit={handleApi}>
                <input
                    value={value.title}
                    name="title"
                    onChange={onChange}
                    placeholder="Enter your title"
                />
                <input 
                    value={value.subtitle}
                    name="subtitle"
                    onChange={onChange}
                    placeholder="Enter your subtitle"
                />
                <input         
                    value={value.category}
                    name="category"
                    onChange={onChange}
                    placeholder="Choose category"
                />
                <input         
                    value={value.upVotes}
                    name="upVotes"
                    onChange={onChange}
                    placeholder="Votes"
                />


                <button onClick={handleApi}>Submit Question</button>
            </form>
    )
}

export default AddQuestion;
